	public void onTestFailure(ITestResult arg0) {
		EventFiringWebDriver edriver=new EventFiringWebDriver(BrowserSelection.driver);
		File srcimg=edriver.getScreenshotAs(OutputType.FILE);
		File destimg=new File("D:/Selenium Workspace/msupply_5/screenshot.png");
		try {
			FileUtils.copyFile(srcimg, destimg);
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

